import React, { useEffect, useState } from "react";
import axios from "axios";

import MoviesList from "./components/MoviesList";
import "./App.css";

function App() {
  const [movies, setMovies] = useState([]);

  const fetchDummyMovies = () => {
    try {
      const response = axios.get("https://swapi.dev/api/films/");
      setMovies(response.results);
    } catch (error) {
      console.log(error);
    } finally {
    }
  };

  useEffect(() => {
    fetchDummyMovies();
  },[])

  return (
    <React.Fragment>
      <section>
        <button>Fetch Movies</button>
      </section>
      <section>
        <MoviesList movies={movies} />
      </section>
    </React.Fragment>
  );
}

export default App;
